Use master;

SELECT * 
FROM sys.symmetric_keys 
WHERE name = '##MS_DatabaseMasterKey##';

CREATE MASTER KEY
ENCRYPTION BY PASSWORD = 'YourStrongPasswordHere!';


CREATE DATABASE SCOPED CREDENTIAL MyADLSCred 
WITH IDENTITY = 'Managed Identity';


---------------------------------------------------------------------------------

CREATE EXTERNAL DATA SOURCE MyParquetData
WITH (
    LOCATION = 'https://storageaccount2025zimmy.dfs.core.windows.net/sales-data',
    CREDENTIAL = MyADLSCred
);

--https://storageaccount2025zimmy.blob.core.windows.net/sales-data

CREATE EXTERNAL FILE FORMAT ParquetFormat
WITH (
    FORMAT_TYPE = PARQUET
);


---------------------------------------------------------------------------------

CREATE VIEW dbo.MyView AS
SELECT *
FROM OPENROWSET(
    BULK 'bronze/*.parquet',
    DATA_SOURCE = 'MyParquetData',
    FORMAT = 'PARQUET'
) AS rows;


Select * from MyView
